script.content:
/*
 * To change this template, choose Tools | Templates
 * and open the template in the editor.
 */

var blogDAO = daoFactory.blogDAO;
var blogs = blogDAO.all;

var size = blogs.size();

println("<h4>Found " + size + " Blogs</h4>");

println("<ul>");
for ( var i=0; i<size; i++ ){
  var blog = blogs.get(i);
  println( "<li>" + blog.getTitle() + "</li>" );
}
println("</ul>");
