script.content:
var commentDAO = daoFactory.commentDAO;
var comments = commentDAO.all;
var size = comments.size();
var comment = null;
var counter = 0;

println("<h3>Spam Report</h3>");
println("<ul>");
for ( i=0; i<size; i++ ){
  comment = comments.get(i);
  if ( comment.spam ){
    counter++;
    println("<li>found spam at "+comment.entry.blog.identifier+"</li>");
  }
}
println("</ul>");
println( "found " + counter + " spam comments" );
